/*忽略不需要构建的文件*/
fis.set('project.ignore',
    [
        'node_modules/**',
        'output/**',
        '.git/**',
        'fis-conf.js',
        'html/**',
        '/html_new/**',
        'Gruntfile.js',
        'package.json',
        'README.Grunt.md'
    ]); // set 为覆盖不是叠加

/*替换需要处理的雪碧图css文件*/


var arr = ['index','other','check_account','check_site_info','clear_sec_pwd','complaint','problem','report_plugin','stolen'];
for( var i in  arr ){
    fis.set( 'namespace'+i, arr[i]);
    fis.media("debug")
        //.match('*.js', {
        //    isMod: false,
        //    release: '/js'
        //})
        .match('/static/modules/${namespace'+i+'}/**.css', {
            packTo: '/css/${namespace'+i+'}.css'
        })
    //.match('/template', {
    //    release: '/template'
    //});
    fis.match('/static/modules/${namespace'+i+'}/css/{images,_images}', {
        release: '/images/${namespace'+i+'}'// fis.set('namespace', 'home'),
    });
    fis.match('/css/${namespace'+i+'}.css', {
        release: '/css/${namespace'+i+'}.css'// fis.set('namespace', 'home'),
    });
}


