/**
 * sq StepAction @VERSION@
 * @author dhf
 * @date 2016-06-12
 *
 * @Depends:
 *  jquery
 *  sq.core.js
 *
 * @Options
 *  {
 *      stepCount: 3,   // 获取3、4、5步联动，默认为3
 *      dataArray: {
 *          [],
 *          [],
 *          []
 *      },              // 需要判断的以及获取的数据数组
 *  }
 *
 *
 */


(function( $, SQ, undefined ) {
    var cache = [],
        StepAction = new SQ.Class( SQ.Widget );

    StepAction.include({
        version: "@VERSION@",
        win: $( window ),
        doc: $( document ),

        eventNamespace: ".sq.StepAction",

        init: function( options ) {
            var opts = this.options = {
                stepCount: 3,
                barWidth: [],
                nextButtons:[],
                prevButtons:[],
                dataArray: {}
            };

            $.extend( this.options, options || {} );

            this._event();

            cache.push( this );
        },
        _event: function(){

        }

    });

    SQ.StepAction = StepAction;

})( jQuery, SQ );