define([
    "modules/modules_common"
],function( mCommon ){

    var mUtil = mCommon.modulesUtil;

    var app = {
            init: function() {
                new SQ.Carousel({
                    el: ".modules-kv",
                    panels: ".modules-kv-li",
                    tabs: "#kvNum li",
                    visible: 1,
                    auto: true,
                    interval: 3000,
                    eventType2: "click mouseenter mouseleave",
                    callback: function( num ) {
                    }
                });
                this.ieHack();
            },
            ieHack: function(){

                if( mUtil.isIE() || mUtil.isIE11() ){
                    $(".modules-type-3").removeClass("hvr-float-shadow").addClass("hvr-glow");
                }

                if( mUtil.isIE6() || mUtil.isIE7() || mUtil.isIE8() ){
                    $(".container-index img").addClass("bigger");
                    $(".modules-5 img").removeClass("bigger")
                }
            }
    };
    return {
        init:function(){
            app.init();
        }
    };
});

