define([],function(){
    var main = {
        init: function() {
            this.tabInit();
        },
        //菜单选项
        tabInit: function(){
            if( SQ.getParam().status == "1" ){
                $(".fwjl-tab-tit").children("li:eq(1)").addClass("fwjl-tab-active");
            }else{
                $(".fwjl-tab-tit").children("li:eq(0)").addClass("fwjl-tab-active");
            }
        }
    };
    return {
        init:function(){
            main.init();
        }
    };
});
