define([],function(){
    var app = {
        init: function() {
            this.leftMenuInit();
        },
        //左侧栏选中事件
        leftMenuInit: function(){
            if( $(".repair_account").hasClass( "ra-index" ) ){
                $("#side-repair-account").addClass("focus");
            }else{
                $("#side-repair-account-result").addClass("focus");
            }

        }
    };
    return {
        init:function(){
            app.init();
        }
    };
});

