define([
    "modules/modules_common"
],function(modulesCommon){
    var modulesUtil = modulesCommon.modulesUtil;
    var main = {
        init: function() {
            this.someProblems();
        },
        //左侧栏选中事件
        someProblems: function(){
            var $someProblems = $(".some-problems");
            if( $someProblems.length > 0){
                modulesUtil.bottomProblems( $someProblems , "zhxf" , 3 );
            }
        }
    };
    return {
        init:function(){
            main.init();
        }
    };
});

